echo "Merge threshold: $MERGE lines"
echo ""

# Opening Prologue (act 0, scene 0)
echo "--- Prologue (Opening) ---"
python "$ANALYZER" "$PLAY" 0 0 --merge "$MERGE" $DRY_RUN

